import React, { useEffect, useState } from 'react';
import API from './services/api';
import SyncButtons from './components/SyncButtons';
import OrderTable from './components/OrderTable';

function App() {
  const [orders, setOrders] = useState([]);

  // Fetch orders from backend
  const fetchOrders = async () => {
    try {
      const res = await API.get('/orders');
      setOrders(res.data);
    } catch (err) {
      console.error('Error fetching orders:', err);
    }
  };

  // Handle retry of failed orders
  const handleRetry = async (orderId) => {
    try {
      await API.post(`/retry/${orderId}`);
      alert(`Retry started for order ${orderId}`);
      fetchOrders();
    } catch (err) {
      console.error('Retry failed:', err);
      alert('Retry failed');
    }
  };

  useEffect(() => {
    fetchOrders();
  }, []);

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Multi-Channel Order Sync Tracker</h1>

      <SyncButtons onSync={fetchOrders} />

      <OrderTable orders={orders} onRetry={handleRetry} />
    </div>
  );
}

export default App;
