import React, { useEffect, useState } from 'react';
import API from './services/api';
import SyncButtons from './components/SyncButtons';
import OrderTable from './components/OrderTable';


function App() {
  const [orders, setOrders] = useState([]);

  const fetchOrders = async () => {
    try {
      const res = await API.get('/orders');
      setOrders(res.data);
    } catch (err) {
      console.error('Error fetching orders:', err);
    }
  };

  const handleRetry = async (orderId) => {
    try {
      await API.post(`/retry/${orderId}`);
      alert(`Retry started for order ${orderId}`);
      fetchOrders();
    } catch (err) {
      console.error('Retry failed:', err);
      alert('Retry failed');
    }
  };

  useEffect(() => {
    fetchOrders();
  }, []);

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Multi-Channel Order Sync Tracker</h1>

      <SyncButtons onSync={fetchOrders} />

      <h2 className="text-xl font-semibold mb-2">Orders</h2>
      <table className="min-w-full border">
        <thead className="bg-gray-200">
          <tr>
            <th className="border px-2 py-1">Order ID</th>
            <th className="border px-2 py-1">Channel</th>
            <th className="border px-2 py-1">Items</th>
            <th className="border px-2 py-1">₹ Value</th>
            <th className="border px-2 py-1">Status</th>
            <th className="border px-2 py-1">Created</th>
            <th className="border px-2 py-1">Action</th>
          </tr>
        </thead>
        <tbody>
          {orders.map((order) => (
            <tr key={order._id}>
              <td className="border px-2 py-1">{order.orderId}</td>
              <td className="border px-2 py-1">{order.channel}</td>
              <td className="border px-2 py-1">{order.items.join(', ')}</td>
              <td className="border px-2 py-1">{order.totalCost} ₹</td>
              <td
                className={`border px-2 py-1 ${
                  order.status === 'success' ? 'text-green-600' :
                  order.status === 'failed' ? 'text-red-600' :
                  'text-yellow-600'
                }`}
              >
                {order.status}
              </td>
              <td className="border px-2 py-1">
                {new Date(order.createdAt).toLocaleString()}
              </td>
              <td className="border px-2 py-1">
                {order.status === 'failed' && (
                  <button
                    onClick={() => handleRetry(order.orderId)}
                    className="bg-yellow-500 text-white px-2 py-1 rounded hover:bg-yellow-600"
                  >
                    Retry
                  </button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
