import React, { useEffect, useState } from 'react';
import API from './services/api';
import SyncButtons from './components/SyncButtons';
import OrderTable from './components/OrderTable';

function App() {
  const [orders, setOrders] = useState([]);

  // Fetch orders from backend
  const fetchOrders = async () => {
    try {
      const res = await API.get('/orders');
      setOrders(res.data);
    } catch (err) {
      console.error('Error fetching orders:', err);
      alert('âŒ Failed to fetch orders');
    }
  };

  // Handle retry of failed orders
  const handleRetry = async (orderId) => {
    try {
      await API.post(`/retry/${orderId}`);
      alert(`âœ… Retry started for order ${orderId}`);
      fetchOrders();
    } catch (err) {
      console.error('Retry failed:', err);
      if (err.response && err.response.data && err.response.data.error) {
        alert(`âŒ Retry failed: ${err.response.data.error}`);
      } else {
        alert('âŒ Retry failed due to server error');
      }
    }
  };

  useEffect(() => {
    fetchOrders();
    const interval = setInterval(fetchOrders, 5000);  // Poll every 5 seconds
    return () => clearInterval(interval);             // Clean up on unmount
  }, []);

  return (
    <div className="p-6 min-h-screen bg-gradient-to-br from-blue-50 to-purple-100">
      <h1 className="text-3xl font-extrabold text-center text-purple-700 mb-6 drop-shadow-sm">
        ðŸš€ Multi-Channel Order Sync Tracker
      </h1>

      <div className="flex justify-center mb-6">
        <SyncButtons onSync={fetchOrders} />
      </div>

      <div className="bg-white rounded-2xl shadow-xl p-4">
        <OrderTable orders={orders} onRetry={handleRetry} />
      </div>
    </div>
  );
}

export default App;
