import React from 'react';
import API from '../services/api';

const OrderTable = ({ orders, onSync }) => {
  const handleRetry = async (orderId) => {
    try {
      await API.post(`/retry/${orderId}`);
      alert(`Retry started for order ${orderId}`);
      onSync();  // Refresh orders after retry
    } catch (err) {
      console.error('Retry failed', err);
      alert('Retry failed');
    }
  };

  return (
    <table className="min-w-full border border-gray-300">
      <thead>
        <tr className="bg-gray-100">
          <th className="p-2 border">Order ID</th>
          <th className="p-2 border">Channel</th>
          <th className="p-2 border">Items</th>
          <th className="p-2 border">Cost (â‚¹)</th>
          <th className="p-2 border">Status</th>
          <th className="p-2 border">Action</th>
        </tr>
      </thead>
      <tbody>
        {orders.map(order => (
          <tr key={order.orderId} className="text-center">
            <td className="p-2 border">{order.orderId}</td>
            <td className="p-2 border">{order.channel}</td>
            <td className="p-2 border">{order.items.join(', ')}</td>
            <td className="p-2 border">{order.totalCost}</td>
            <td className={`p-2 border ${
              order.status === 'success' ? 'text-green-600' :
              order.status === 'failed' ? 'text-red-600' :
              'text-yellow-600'
            }`}>
              {order.status}
            </td>
            <td className="p-2 border">
              {order.status === 'failed' && (
                <button
                  onClick={() => handleRetry(order.orderId)}
                  className="bg-yellow-500 text-white px-2 py-1 rounded hover:bg-yellow-600"
                >
                  Retry
                </button>
              )}
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default OrderTable;
