import React, { useEffect, useState } from 'react';
import API from './services/api';
import SyncButtons from './components/SyncButtons';

function App() {
  const [orders, setOrders] = useState([]);

  const fetchOrders = async () => {
    try {
      const res = await API.get('/orders');
      setOrders(res.data);
    } catch (err) {
      console.error('Error fetching orders:', err);
    }
  };

  useEffect(() => {
    fetchOrders();
  }, []);

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Multi-Channel Order Sync Tracker</h1>

      <SyncButtons onSync={fetchOrders} />

      <h2 className="text-xl font-semibold mb-2">Orders</h2>
      <table className="min-w-full border">
        <thead className="bg-gray-200">
          <tr>
            <th className="border px-2">Order ID</th>
            <th className="border px-2">Channel</th>
            <th className="border px-2">Items</th>
            <th className="border px-2">â‚¹ Value</th>
            <th className="border px-2">Status</th>
            <th className="border px-2">Created</th>
          </tr>
        </thead>
        <tbody>
          {orders.map((order) => (
            <tr key={order._id}>
              <td className="border px-2">{order.orderId}</td>
              <td className="border px-2">{order.channel}</td>
              <td className="border px-2">{order.items.join(', ')}</td>
              <td className="border px-2">{order.totalCost} {order.currency}</td>
              <td className="border px-2">{order.status}</td>
              <td className="border px-2">{new Date(order.createdAt).toLocaleString()}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
